import { configureStore } from "@reduxjs/toolkit";
import CartReducer from "./slices/CartSlice";  // ✅ Import the reducer, not the whole slice

export const store = configureStore({
    reducer: {
        cart: CartReducer  // ✅ Use only the reducer here
    },
});
